import React, { useState, useEffect } from 'react';
import axios from 'axios';
import ListGroup from 'react-bootstrap/ListGroup';
import Spinner from 'react-bootstrap/Spinner';
import { withRouter } from 'react-router-dom';
import Button from 'react-bootstrap/Button';

function RewardList(props) {
  const [data, setData] = useState([]);
  const [showLoading, setShowLoading] = useState(true);
  const apiUrl = "https://localhost:44348/api/rewards";

  useEffect(() => {
    // const fetchData = async () => {
    //   // const result = await axios(apiUrl);
      fetch(apiUrl).then(res => res.json())
      .then((data) => {
        console.log(data)
        setData(data);
        setShowLoading(false);
      })
      .catch(console.log)

    // };
    // fetchData();
  }, []);

  const showDetail = (id) => {
    props.history.push({
      pathname: '/showreward/' + id
    });
  }

  const addNew = () => {
    props.history.push({
      pathname: '/createreward/'
    });
  }

  return (
    <div>
      {showLoading && <Spinner animation="border" role="status">
        {/* <span className="sr-only">Loading...</span> */}
      </Spinner> }
      <Button variant="primary" onClick={() => { addNew() }} style={{width: 1000, marginTop: 20, marginLeft: 250}}>
            Add New Reward
          </Button>
      <ListGroup>
        {data.map((item, idx) => (
          <ListGroup.Item key={idx} action onClick={() => { showDetail(item.documentId) }}>{item.brand}</ListGroup.Item>
        ))}
      </ListGroup>
    </div>
  );
}

export default withRouter(RewardList);
